package com.example.vpn\n\nimport android.content.Intent\nimport android.os.Bundle\nimport androidx.appcompat.app.AppCompatActivity\nimport android.widget.Button\nimport android.widget.TextView\n\nclass AppOnboardingActivity : AppCompatActivity() {\n    private lateinit var nextButton: Button\n    private lateinit var infoText: TextView\n    private var currentScreen = 0\n\n    private val onboardingScreens = arrayOf(\n        "Welcome! Let's get started with our VPN.\n        \n        This screen will explain how our VPN provides privacy and security online.",\n        "Chat Rules:\n        \n        Make sure to abide by our community guidelines while using the chat feature.",\n        "Message Filtering Policies:\n        \n        We have strict policies in place to filter spam and harmful messages.",\n        "Notification Settings:\n        \n        Customize your notification preferences for a better experience."\n    )\n\n    override fun onCreate(savedInstanceState: Bundle?) {\n        super.onCreate(savedInstanceState)\n        setContentView(R.layout.activity_onboarding)\n        nextButton = findViewById(R.id.nextButton)\n        infoText = findViewById(R.id.infoText)\n\n        updateContent()\n\n        nextButton.setOnClickListener { nextScreen() }\n    }\n\n    private fun updateContent() {\n        infoText.text = onboardingScreens[currentScreen]\n    }\n\n    private fun nextScreen() {\n        currentScreen++\n        if (currentScreen >= onboardingScreens.size) {\n            finishOnboarding()\n        } else {\n            updateContent()\n        }\n    }\n\n    private fun finishOnboarding() {\n        // Here you will navigate to the main functionality of the app\n        startActivity(Intent(this, MainActivity::class.java))\n        finish()\n    }\n}